import { Component } from '@angular/core';

@Component({
  selector: 'app-share-code',
  templateUrl: './share-code.component.html',
  styleUrls: ['./share-code.component.css']
})
export class ShareCodeComponent {
  public userName: string= '';
  public user: string='';
  public roomCode: string='';
}
<div class="container">
    <img src="../../assets/images/bg.jpeg" alt="background image" style="width:100%;">
    <div class="centered">
        <div class="circle-container" >
            <img src="" alt="Photo"> {{ userName}}
            <p class = "user">{{ user }}</p>
        </div>
        <div class = "box2">
            <label for="code" class = "code">Share the following room code with your friends</label><br>
            <input type = "text" placeholder="-Type here-" class = "playerRoom"  [(ngModel)]="roomCode"><br>
        </div>
            <button class = "nexttBtn">Next</button>

    </div>   
</div>
