import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-create-room',
  templateUrl: './create-room.component.html',
  styleUrls: ['./create-room.component.css']
})
export class CreateRoomComponent {
  public user: string = '';
  public userName: string= '';

  constructor(private route: ActivatedRoute , private router: Router) {}
  ngOnInit() {
    this.route.queryParams.subscribe((params) => {
      this.userName = params['data2'];
    });
}
}
<div class="container">
    <img src="../../assets/images/bg.jpeg" alt="background image" style="width:100%;">
    <div class="centered">
        <div class="circle-container" >
            <img src="" alt="Photo"> {{ userName}}
            <p class = "user">{{ user }}</p>
        </div>
            <label for="roomName" class = "roomName">Room Name</label><br>
            <input type = "text" placeholder="-Type here-" class = "playerRoom" ><br>
            <label for="players" class = "players">No. of players</label><br>
            <input type = "text" placeholder="-Type here-" class = "playerRoom2" ><br>
            <button class = "" >Next</button>

    </div>   
</div>
