import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-join-room',
  templateUrl: './join-room.component.html',
  styleUrls: ['./join-room.component.css']
})
export class JoinRoomComponent {
  public user: string = '';
  public userName: string= '';
public roomCode: string = '';


constructor(private route: ActivatedRoute , private router: Router) {}
ngOnInit() {
  this.route.queryParams.subscribe((params) => {
    this.userName = params['data3'];
  });
}
}
<div class="container">
    <img src="../../assets/images/bg.jpeg" alt="background image" style="width:100%;">
    <div class="centered">
        <div class="circle-container" >
            <img src="" alt="Photo"> {{ userName}}
            <p class = "user">{{ user }}</p>
        </div>
        <label for="roomCode" class = "rommCode">Type room code below</label><br>
            <input type = "text" placeholder="-Type-" class = "playerRoom"  [(ngModel)]="roomCode"><br>
            <button class = "Btn"  [disabled]="!roomCode" [ngClass]="{'enabled': roomCode}" >Next</button>

    </div>   
</div>
