import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-create-room',
  templateUrl: './create-room.component.html',
  styleUrls: ['./create-room.component.css']
})
export class CreateRoomComponent {
  public user: string = '';
  public userProfile: string= '';
  public roomName: string = '';
  public players: number = 0;

  constructor(private route: ActivatedRoute , private router: Router) {}
  ngOnInit() {
}
createBtn(){
console.log('clicked');
    this.router.navigate(['/codeRoom']);
  }
}

<div class="container">
    <img src="../../assets/images/bg.jpeg" alt="background image" style="width:100%;">
    <div class="centered">
        <div class="circle-container" >
            <img [src]= "userProfile" alt="Photo">
            <p class = "user">{{ user }}</p>
        </div>
            <input type = "text" placeholder="-Room Name-" class = "playerRoom"  [(ngModel)]="roomName"><br>
            <input type="number" name="players" [(ngModel)]="players"
             onkeydown="if(parseInt(players + event.key) > 5) event.preventDefault();" min="1" max="5" required>
            <button (click) = "createBtn()" class = "createBtn"  [disabled]="!roomName && !players" [ngClass]="{'enabled': roomName && players}" >Create</button>

    </div>   
</div>
