import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-create-room',
  templateUrl: './create-room.component.html',
  styleUrls: ['./create-room.component.css']
})
export class CreateRoomComponent {
  public user: string = '';
  public userName: string= '';
  public roomName: string = '';
  public players: undefined;

  constructor(private route: ActivatedRoute , private router: Router) {}
  ngOnInit() {
}
}
<div class="container">
    <img src="../../assets/images/bg.jpeg" alt="background image" style="width:100%;">
    <div class="centered">
        <div class="circle-container" >
            <img src="" alt="Photo"> {{ userName}}
            <p class = "user">{{ user }}</p>
        </div>
            <input type = "text" placeholder="-Room Name-" class = "playerRoom"  [(ngModel)]="roomName"><br>
            <input type = "text" placeholder="-No of players-" class = "playerRoom2"  [(ngModel)]="players"><br>
            <button class = "createBtn"  [disabled]="!roomName" [ngClass]="{'enabled': roomName}" >Create</button>

    </div>   
</div>
