import { Component } from '@angular/core';

@Component({
  selector: 'app-share-code',
  templateUrl: './share-code.component.html',
  styleUrls: ['./share-code.component.css']
})
export class ShareCodeComponent {
  public userName: string= '';
  public user: string='';
  public roomCode: string='';
}
<div class="container">
    <img src="../../assets/images/bg.jpeg" alt="background image" style="width:100%;">
    <div class="centered">
        <input type = "value"  class = "playerRoom"  [(ngModel)]="roomCode"><br>
        <div class="circle-container" >
        <img src="" alt="Photo"> {{ userName}}
        </div>
            <button class = "startBtn">start</button>

    </div>   
</div>
