import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-hello-user',
  templateUrl: './hello-user.component.html',
  styleUrls: ['./hello-user.component.css'],
})
export class HelloUserComponent {
  public userName: string = '';
  public imageUrl: string = '';
  public imageSelected: boolean = false;

  constructor(private route: ActivatedRoute) {}
  ngOnInit() {
    this.route.queryParams.subscribe((params) => {
      this.userName = params['data'];
    });
  }
  onImageClick(url: string) {
    this.imageUrl = url;
    console.log(this.imageUrl);
    this.imageSelected = true;
  }
}
<div class="container">
    <img src="../../assets/images/bg.svg" alt="background image" style="width:100%;">
<div class="centered">

  <p>Hello {{ userName }}</p>
  <p>Chooose Your Avtar</p>
  <div class="circle-container">
    <img
      src="../../assets/images/avtar1.svg"
      class="avtImg1"
      alt="button"
      (click)="onImageClick('../../assets/images/avtar1.svg')"
    />
    <img
      src="../../assets/images/avtar2.svg"
      class="avtImg1"
      alt="avtar2"
      (click)="onImageClick('../../assets/images/avtar2.svg')"
    />
    <img
      src="../../assets/images/avtar3.svg"
      class="avtImg1"
      alt="avtar3"
      (click)="onImageClick('../../assets/images/avtar3.svg')"
    />
    <img
      src="../../assets/images/avtar4.svg"
      class="avtImg1"
      alt="avtar4"
      (click)="onImageClick('../../assets/images/avtar4.svg')"
    />
    <img
      src="../../assets/images/avtar5.svg"
      class="avtImg1"
      alt="avtar5"
      (click)="onImageClick('../../assets/images/avtar5.svg')"
    />
  </div>
  <button type="button" [disabled] = "!imageSelected" >Next</button>
</div>
</div>
